mapper.select:
select id
from department
where name = #{name}

select did
from technology
where id = #{id}

select distinct `volume_tec` as tec
from technology t,
     workday_tec w,
     department d
where
   d.name = #{name}
  and d.id = t.did
  and t.name = w.volume_tec
  and w.project_id = #{id}

select distinct `volume_tec` as tec
from technology t,
     workday_tec w,
     workday_tec_principal p,
     department d
where
   d.name = #{name}
  and d.id = t.did
  and t.name = w.volume_tec
  and p.workday_tec_id = w.id
  and p.user_id = #{userId}
  and w.project_id = #{id}
