mapper.select:
SELECT
SUM(d.WorkTime)/8
FROM
[dbo].[TMS_WorkTime] w,
[dbo].[TMS_WorkTimeDetail] d
WHERE w.WorkTimeID = d.WorkTimeID
AND w.WorkDate < #{endDate}
AND w.WorkDate >= #{startDate}
AND d.ParentDeptName = #{department}

SELECT
SUM(d.WorkTime)/8 as workday, w.LoginId, w.UserName
FROM
[dbo].[TMS_WorkTime] w,
[dbo].[TMS_WorkTimeDetail] d
WHERE w.WorkTimeID = d.WorkTimeID
AND w.WorkDate < #{endDate}
AND w.WorkDate >= #{startDate}
AND d.ParentDeptName = #{department}
GROUP BY w.LoginId, w.UserName
